sp_widget.client_script:
api.controller=function($scope, $rootScope) {
  /* widget controller */
  var c = this;

	//si mette in attesa dell'evento selectNote
	//c.server.get() chiama il Server Script e passa un input custom
	//la chiamata restituisce una promise, 
	//e quando la riceve, tramite il .then esegue una funzione
	$rootScope.$on('selectNote', function(event,data) {
		c.server.get({
			action: 'getNote',
			noteID: $rootScope.noteID
		}).then(function(r){
			c.data.title = r.data.note.title;
			c.data.note = r.data.note.note;
			c.data.noteID = r.data.note.sys_id;
		});
	});

	//sull'ng-change viene attivata la funzione updateNote che chiama lo scriptServer
	//e gli passa l'input custom
	c.updateNote = function(updateType) {
		c.server.get({
			action: 'updateNote',
			noteID: c.data.noteID,
			noteTitle: c.data.title,
			noteBody: c.data.note,
		}).then(function(){

		});
	}
};

sp_widget.script:
(function() {
  /* populate the 'data' object */
  /* e.g., data.table = $sp.getValue('table'); */
	if(input && input.noteID){
		var note = new GlideRecord('x_snc_createnotes_note');
		if(note.get(input.noteID)) {
			if(input.action=='getNote'){
				data.note = {};
				$sp.getRecordValues(data.note, note, "title, note, sys_id");
			} else if(input.action=='updateNote'){
				note.title = input.noteTitle;
				note.note = input.noteBody;
				note.update();
			}
		}
	}

})();
